import UserTable from './Components/UserTable';
import AddUseForm from './Components/AddUserForm';
import { v4 as uuidv4 } from 'uuid'
import React, { useState } from 'react';
import './App.css';

function App() {
  const userData = [
    { id: uuidv4(), name: 'b1', userName: 'c1' },
    { id: uuidv4(), name: 'b2', userName: 'c2' },
    { id: uuidv4(), name: 'b3', userName: 'c3' }
  ]

  const [users, setUsers] = useState(userData);
  const [editing, seteEditing] = useState(false);
  const [currentUsers, setCurrentUsers] = useState({
    id:null,name:'',userName:''
  });
  const addUsers = (user) => {
    {
      user.id = uuidv4;
      setUsers([
        ...users,
        user
      ]
      )
    }
  }
  const editRow=(user)=>{

    setCurrentUsers({
      id:user.name,
      name:user.name,
      userName:user.userName
    })
  }
  return (
    <div className="container">
      <div>
        {
          editing ? (
            <div>

              console.log('lol')
              <AddUseForm addUsers={addUsers} seteEditing={true} msg={'Editar Usuarios'}/>
            </div>
          ) : (
            <AddUseForm addUsers={addUsers} msg={'Agregar Usuarios'}/>
          )
        }
        <UserTable users={users} editRow={editRow}/>
      </div>
    </div>
  );
}

export default App;
