import UserTable from './Components/UserTable';
import AddUseForm from './Components/AddUserForm';
import EditUseForm from './Components/EditUseForm';
import { v4 as uuidv4 } from 'uuid'
import React, { useState } from 'react';
import './App.css';

function App() {

  const userData = [
    { id: uuidv4(), name: 'b1', userName: 'c1' },
    { id: uuidv4(), name: 'b2', userName: 'c2' },
    { id: uuidv4(), name: 'b3', userName: 'c3' }
  ]

  const [users, setUsers] = useState(userData);
  const [editing, setEditing] = useState(false);
  const [currentUser, setCurrentUser]= useState({
    id: null, name: '', userName: ''
  });

  const editRow=(user)=>{
    setEditing(true)
    setCurrentUser(
      {id:user.id, name:user.name, userName:user.userName}
      )
  }
  const updateUser=(id, updateUser)=>{
    setEditing(true) 
    setUsers(users.map(user=>(id===user.id ? updateUser: user)))
  }
  const deleteUser=(id)=>{
    setUsers(users.filter(user=> id !== user.id))
  }

  const addUsers = (user) => 
    {
      user.id = uuidv4;
      setUsers([
        ...users,
        user
      ]
      )
    }


  return (
    <div className="container">
      <div>
        {
          editing ? (
            <div>                            
              <EditUseForm msg={'Editar Usuarios'} currentUser={currentUser} updateUser={updateUser} setEditing={true}/>
            </div>
          ) : (
            //<AddUseForm addUsers={addUsers} msg={'Agregar Usuarios'} />
            <Agregar msg={'Agregar Usuarios'}/>
          )
        }
        <UserTable users={users} editRow={editRow} deleteUser={deleteUser} /> 

      </div>
    </div>
  );
}

export default App;
